import React from "react";
import { Link } from "react-router-dom";
import "./Cards.css";

function CardItem(props) {
  return (
      <li className="">
        <Link className="" to={props.path}>
          <div >
            <figure className="">
              <img src={props.src} alt="Travel" className=""/>
            </figure>
            <div className="">
              <h5 className=""> {props.text}</h5>
            </div>
          </div>
        </Link>
      </li> 
  );
}

export default CardItem;
